mapper.insert:
INSERT INTO rcp (
rcp_name, rcp_img_url
) VALUES (
#{rcpName}, #{rcpImgUrl}
)

INSERT INTO rcp_ing (
rcp_id, ing_id, rcp_ing_amt, rcp_ing_ord
) VALUES (
#{rcpId}, #{ingId}, #{rcpIngAmt}, #{rcpIngOrd}
)

INSERT INTO rcp_steps (
rcp_id, step_no, step_desc, step_img_url
) VALUES (
#{rcpId}, #{stepNo}, #{stepDesc}, #{stepImgUrl}
)

mapper.update:
UPDATE rcp
SET rcp_view_count = COALESCE(rcp_view_count, 0) + 1
WHERE rcp_id = #{rcpId}

mapper.select:
SELECT recipe_counts.rcp_id, recipe_counts.recipe_count, COALESCE(user_counts.my_count, 0) as my_count FROM ( SELECT ri.rcp_id, COUNT(ri.ing_id) as recipe_count FROM rcp_ing ri WHERE ri.rcp_id IN
#{rcpId}
GROUP BY ri.rcp_id ) recipe_counts LEFT JOIN ( SELECT ri.rcp_id, COUNT(ri.ing_id) as my_count FROM rcp_ing ri INNER JOIN user_ing ui ON ui.ing_id = ri.ing_id WHERE ri.rcp_id IN
#{rcpId}
AND ui.user_id = #{userId} GROUP BY ri.rcp_id ) user_counts ON recipe_counts.rcp_id = user_counts.rcp_id
