mapper.insert:
INSERT INTO user (
user_name, user_email, user_role,
oauth2Provider, oauth2Id
) VALUES (
#{userName}, #{userEmail}, #{userRole},
#{oauth2Provider}, #{oauth2Id}
)

mapper.select:
SELECT
user_id, user_name, user_email, user_role,
oauth2Provider, oauth2Id, createdAt, updatedAt
FROM user


        (user_name LIKE CONCAT('%', #{keyword}, '%')
        OR user_email LIKE CONCAT('%', #{keyword}, '%')
        OR oauth2Provider LIKE CONCAT('%', #{keyword}, '%'))


        AND user_role = #{role}


        AND oauth2Provider = #{provider}


        AND createdAt >= #{startDate}


        AND createdAt <= #{endDate}


ORDER BY createdAt DESC
